settings.custom_liquid:
{% assign today_wday = 'now' | date: "%w" | plus: 0 %}
{% assign today_ts = 'now' | date: "%s" | plus: 0 %}

{% case today_wday %}
  {% when 1 %} {% assign days_until_delivery = 2 %}
  {% when 2 %} {% assign days_until_delivery = 2 %}
  {% when 3 %} {% assign days_until_delivery = 2 %}
  {% when 4 %} {% assign days_until_delivery = 4 %}
  {% when 5 %} {% assign days_until_delivery = 3 %}
  {% when 6 %} {% assign days_until_delivery = 3 %}
  {% when 0 %} {% assign days_until_delivery = 2 %}
{% endcase %}

{% assign delivery_seconds = days_until_delivery | times: 86400 %}
{% assign delivery_timestamp = today_ts | plus: delivery_seconds %}
{% assign day_name = delivery_timestamp | date: "%A" %}
{% assign day_number = delivery_timestamp | date: "%e" %}
{% assign month_name = delivery_timestamp | date: "%B" %}

{% case request.locale.iso_code %}

  {% when "nl" %}
    {% case day_name %}
      {% when "Monday" %} {% assign day_name = "Maandag" %}
      {% when "Tuesday" %} {% assign day_name = "Dinsdag" %}
      {% when "Wednesday" %} {% assign day_name = "Woensdag" %}
      {% when "Thursday" %} {% assign day_name = "Donderdag" %}
      {% when "Friday" %} {% assign day_name = "Vrijdag" %}
      {% when "Saturday" %} {% assign day_name = "Zaterdag" %}
      {% when "Sunday" %} {% assign day_name = "Zondag" %}
    {% endcase %}
    {% case month_name %}
      {% when "January" %} {% assign month_name = "januari" %}
      {% when "February" %} {% assign month_name = "februari" %}
      {% when "March" %} {% assign month_name = "maart" %}
      {% when "April" %} {% assign month_name = "april" %}
      {% when "May" %} {% assign month_name = "mei" %}
      {% when "June" %} {% assign month_name = "juni" %}
      {% when "July" %} {% assign month_name = "juli" %}
      {% when "August" %} {% assign month_name = "augustus" %}
      {% when "September" %} {% assign month_name = "september" %}
      {% when "October" %} {% assign month_name = "oktober" %}
      {% when "November" %} {% assign month_name = "november" %}
      {% when "December" %} {% assign month_name = "december" %}
    {% endcase %}
    {% assign delivery_date = day_name | append: " " | append: day_number | append: " " | append: month_name %}
    {% assign delivery_message = "Levering op <strong>" | append: delivery_date | append: "</strong>" %}

  {% when "fr" %}
    {% case day_name %}
      {% when "Monday" %} {% assign day_name = "Lundi" %}
      {% when "Tuesday" %} {% assign day_name = "Mardi" %}
      {% when "Wednesday" %} {% assign day_name = "Mercredi" %}
      {% when "Thursday" %} {% assign day_name = "Jeudi" %}
      {% when "Friday" %} {% assign day_name = "Vendredi" %}
      {% when "Saturday" %} {% assign day_name = "Samedi" %}
      {% when "Sunday" %} {% assign day_name = "Dimanche" %}
    {% endcase %}
    {% case month_name %}
      {% when "January" %} {% assign month_name = "janvier" %}
      {% when "February" %} {% assign month_name = "f√©vrier" %}
      {% when "March" %} {% assign month_name = "mars" %}
      {% when "April" %} {% assign month_name = "avril" %}
      {% when "May" %} {% assign month_name = "mai" %}
      {% when "June" %} {% assign month_name = "juin" %}
      {% when "July" %} {% assign month_name = "juillet" %}
      {% when "August" %} {% assign month_name = "ao√ªt" %}
      {% when "September" %} {% assign month_name = "septembre" %}
      {% when "October" %} {% assign month_name = "octobre" %}
      {% when "November" %} {% assign month_name = "novembre" %}
      {% when "December" %} {% assign month_name = "d√©cembre" %}
    {% endcase %}
    {% assign delivery_date = day_name | append: " " | append: day_number | append: " " | append: month_name %}
    {% assign delivery_message = "Livraison le <strong>" | append: delivery_date | append: "</strong>" %}

  {% when "de" %}
    {% case day_name %}
      {% when "Monday" %} {% assign day_name = "Montag" %}
      {% when "Tuesday" %} {% assign day_name = "Dienstag" %}
      {% when "Wednesday" %} {% assign day_name = "Mittwoch" %}
      {% when "Thursday" %} {% assign day_name = "Donnerstag" %}
      {% when "Friday" %} {% assign day_name = "Freitag" %}
      {% when "Saturday" %} {% assign day_name = "Samstag" %}
      {% when "Sunday" %} {% assign day_name = "Sonntag" %}
    {% endcase %}
    {% case month_name %}
      {% when "January" %} {% assign month_name = "Januar" %}
      {% when "February" %} {% assign month_name = "Februar" %}
      {% when "March" %} {% assign month_name = "M√§rz" %}
      {% when "April" %} {% assign month_name = "April" %}
      {% when "May" %} {% assign month_name = "Mai" %}
      {% when "June" %} {% assign month_name = "Juni" %}
      {% when "July" %} {% assign month_name = "Juli" %}
      {% when "August" %} {% assign month_name = "August" %}
      {% when "September" %} {% assign month_name = "September" %}
      {% when "October" %} {% assign month_name = "Oktober" %}
      {% when "November" %} {% assign month_name = "November" %}
      {% when "December" %} {% assign month_name = "Dezember" %}
    {% endcase %}
    {% assign delivery_date = day_name | append: " " | append: day_number | append: ". " | append: month_name %}
    {% assign delivery_message = "Lieferung bis <strong>" | append: delivery_date | append: "</strong>" %}

  {% else %}
    {% assign formatted_date = delivery_timestamp | date: "%A %e %B" %}
    {% assign delivery_message = "Delivery by <strong>" | append: formatted_date | append: "</strong>" %}

{% endcase %}

<style>
  .el-cartel-delivery {
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 14px;
    margin-top: 15px;
    color: #121212;
    font-family: inherit;
  }

  .el-cartel-dot {
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background-color: #00e676;
    margin-right: 10px;
    animation: soft-flicker 1.6s infinite;
    box-shadow: 0 0 6px rgba(0, 230, 118, 0.8);
  }

  @keyframes soft-flicker {
    0%, 100% {
      opacity: 1;
      box-shadow: 0 0 6px rgba(0, 230, 118, 0.8);
    }
    50% {
      opacity: 0.4;
      box-shadow: 0 0 2px rgba(0, 230, 118, 0.4);
    }
  }
</style>

<div class="el-cartel-delivery">
  <span class="el-cartel-dot"></span>
  <span>{{ delivery_message }}</span>
</div>
